SET NOCOUNT ON;

DECLARE @BatchSize INT = 8000;
DECLARE @RowsAffected INT;

-- Repeat until no rows are affected
WHILE 1 = 1
BEGIN
    DELETE  TOP (@BatchSize) FROM @{item().SourceSchemaName}.@{item().SourceTableName} with (rowlock) WHERE CAST(@{item().KeyColumn} AS DATE) < DATEADD(DAY, -@{item().ArchiveDays}, CAST(GETDATE() AS DATE))
    SET @RowsAffected = @@ROWCOUNT; 
    WAITFOR DELAY '00:00:01'
    IF @RowsAffected = 0
        BREAK; -- Exit the loop when no more rows are deleted
END

Declare @userAPI varchar(36)
set @userAPI=(select objectguid from userobjects with (readpast) where sAMAccountName='APIPUSHUSER');

Declare @startDate datetime=getDate();
print @startDate
DECLARE @done bit = 0;  
WHILE (@done = 0)  
BEGIN  
    DELETE TOP(5000)
	FROM RefreshTokens with (ROWLOCK)
	WHERE ExpiresUtc < GETUTCDATE() or UserId=@userAPI
    IF @@rowcount < 1 SET @done = 1;  
	WAITFOR DELAY '00:00:02';
END;
