SET NOCOUNT ON;

DECLARE @BatchSize INT = 8000;
DECLARE @RowsAffected INT;

-- Repeat until no rows are affected
WHILE 1 = 1
BEGIN
    DELETE  TOP (@BatchSize) FROM @{item().SourceSchemaName}.@{item().SourceTableName} with (rowlock) WHERE CAST(@{item().KeyColumn} AS DATE) < DATEADD(DAY, -@{item().ArchiveDays}, CAST(GETDATE() AS DATE))
    SET @RowsAffected = @@ROWCOUNT; 
    WAITFOR DELAY '00:00:01'
    IF @RowsAffected = 0
        BREAK; -- Exit the loop when no more rows are deleted
END
