Declare @userAPI varchar(36)
set @userAPI=(select objectguid from userobjects with (readpast) where sAMAccountName='APIPUSHUSER');

Declare @startDate datetime=getDate();
print @startDate
DECLARE @done bit = 0;  
WHILE (@done = 0)  
BEGIN  
    DELETE TOP(5000)
	FROM RefreshTokens with (ROWLOCK)
	WHERE ExpiresUtc < GETUTCDATE() or UserId=@userAPI
    IF @@rowcount < 1 SET @done = 1;  
	WAITFOR DELAY '00:00:02';
END;
