DECLARE @BatchSize INT = 10000;
DECLARE @RowsAffected INT;


WHILE 1 = 1
BEGIN
    DELETE TOP (@BatchSize)
    FROM activityLogs  with (RowLock)
    WHERE  Cast(datetime as date) <= Cast(getdate()-11 as date)

    SET @RowsAffected = @@ROWCOUNT; 

	WAITFOR DELAY '00:00:01';
    IF @RowsAffected = 0
        BREAK; 
END

