DECLARE @BatchSize INT = 10000; 
DECLARE @RowsAffected INT = 1; 
DECLARE @TotalDeletedRows INT = 0; 

WHILE (@RowsAffected > 0)
BEGIN
    DELETE 
	FROM 
		[dbo].[LoanTransactionsDetails]
	WHERE 
		[PostingDate]  < DATEADD(DAY,-15,CAST(GETUTCDATE() AS DATE));

    SET @RowsAffected = @@ROWCOUNT; 
    SET @TotalDeletedRows = @TotalDeletedRows + @RowsAffected;
END


SELECT @TotalDeletedRows AS TotalDeletedRows;

