typeProperties.body:
{
  "personalizations": [
    {
      "to": [{ "email": "@{pipeline().parameters.EmailTo}" }]
    }
  ],
  "from": {
    "email": "@{pipeline().globalParameters.FromEmail}"
  },
  "subject": "@{concat('Pipeline ', pipeline().Pipeline, ' - ', if(equals(pipeline().TriggerType, 'Failure'), 'Failure', 'Success'))}",
  "content": [
    {
      "type": "text/plain",
      "value": "@{concat(
        'Pipeline Name: ', pipeline().Pipeline, '\n',
        'Status: ', if(equals(pipeline().TriggerType, 'Failure'), 'Failure', 'Success'), '\n',
        'Importance: ', if(equals(pipeline().TriggerType, 'Failure'), 'High', 'Normal'), '\n',
        'Data Factory Name: ', pipeline().DataFactory, '\n',
        if(equals(pipeline().TriggerType, 'Failure'), concat('Failed Activity: ', activity('YourActivityName').Error.activityName, '\n'), ''),
        'Message: ', pipeline().parameters.Message, '\n'
      )}"
    }
  ],
  "custom_args": {
    "DataFactoryName": "@{pipeline().DataFactory}",
    "PipelineName": "@{pipeline().Pipeline}",
    "Status": "@{if(equals(pipeline().TriggerType, 'Failure'), 'Failure', 'Success')}"
  }
}
