@DECLARE BatchSize INT = 8000;
DECLARE RowsAffected INT;

WHILE 1 = 1
BEGIN
    DELETE TOP (BatchSize)
    FROM Adaptive.UserActivityLog with (RowLock)
    WHERE  CAST(CreatedOn AS DATE) <= DATEADD(DAY, -pipeline().parameters.DaysOld, CAST(GETDATE() AS DATE))

    SET RowsAffected = ROWCOUNT;  
    Print RowsAffected
	WAITFOR DELAY '00:00:02';
    IF RowsAffected = 0
        BREAK;  
END
