DECLARE @BatchSize INT = 8000;
DECLARE @RowsAffected INT;
DECLARE @DaysOld INT = @{pipeline().parameters.Daysold};  -- ADF parameter

WHILE 1 = 1
BEGIN
    DELETE TOP (@BatchSize)
    FROM Adaptive.UserActivityLog WITH (ROWLOCK)
    WHERE CAST(CreatedOn AS DATE) <= DATEADD(DAY, -@DaysOld, CAST(GETDATE() AS DATE));

    SET @RowsAffected = @@ROWCOUNT;  
    PRINT @RowsAffected;

    WAITFOR DELAY '00:00:02';

    IF @RowsAffected = 0
        BREAK;  
END
